import 'package:flutter/material.dart';import 'package:flutter_basics/core/app_cubit/app_cubit.dart';import 'package:flutter_basics/core/di/injector.dart';import 'package:flutter_basics/core/observer/cubit_observer.dart';import 'package:flutter_bloc/flutter_bloc.dart';/// [AppConfig] is used for All configurationclass AppConfig {  /// Private constructor  AppConfig._();  /// Creating instance of App Config  static final instance = AppConfig._();  /// Initialise all thing at App Launch  void initialise() {    WidgetsFlutterBinding.ensureInitialized();    /// Assigning our Cubit Observer to [Bloc.observer] for continue observe    Bloc.observer = CubitObserver();    /// Register AppCubit    /// This will create App Cubit    Injector.instance.registerSingleton(AppCubit());  }}