import 'package:flutter/material.dart';import 'package:flutter_basics/core/theme/theme_color.dart';/// Extension on [BuildContext]extension ExtBuildContext on BuildContext {  /// To Access Theme  ThemeData get theme => Theme.of(this);  /// To Access App Color  AppColors get appColor => Theme.of(this).extension<AppColors>()!;  /// To access Text style  TextTheme get text => Theme.of(this).textTheme;  /// Device width  double get width => MediaQuery.sizeOf(this).width;  /// Device height  double get height => MediaQuery.sizeOf(this).height;}