import 'package:flutter/foundation.dart';import 'package:logger/logger.dart';/// Logger Instancefinal logger = Logger();/// Extension on String for simplify useextension ExtLogger on String {  /// Debug Log  void get logD => logger.d(this);  /// Info Log  void get logI => logger.i(this);  /// Warning Log  void get logW => logger.w(this);  /// Error Log  void get logE => logger.e(this);}/// [LogFilter] is for when to log will print or notclass LoggerFilter extends LogFilter {  @override  bool shouldLog(LogEvent event) => kDebugMode;}