import 'package:flutter/material.dart';import 'package:flutter_basics/core/enums/property_type.dart';/// Widget Property Modelclass WidgetPropertyModel<T> {  /// Default constructor  const WidgetPropertyModel({    required this.title,    required this.type,    this.textFieldProperty,    this.properties,    this.dropDownProperty,  })  : assert(type != PropertyType.textField || textFieldProperty != null,            'Text field property required for Text Field'),        assert(type != PropertyType.dropDown || dropDownProperty != null,            'Drop down property required for Drop down Field');  /// Widget property Title  final String title;  /// Widget property Type  final PropertyType type;  /// Text Field Property  final TextFieldProperty? textFieldProperty;  /// Dropdown Property  final DropDownProperty<T>? dropDownProperty;  /// List of Properties  final List<WidgetPropertyModel<T>>? properties;}/// Text Field Propertyclass TextFieldProperty {  /// Default constructor  const TextFieldProperty({    required this.controller,    required this.hintText,    this.labelText,    this.maxLines = 1,  });  /// Text Field Label Text  final String? labelText;  /// Text Field Hint Text  final String hintText;  /// Text Editing controller  final TextEditingController controller;  /// Text Field Max Lines  final int maxLines;}/// Dropdown Propertyclass DropDownProperty<T> {  /// Default constructor  const DropDownProperty(      {required this.items, this.selectedItem, this.labelText, this.onChanged});  /// List of Items  final List<DropdownMenuItem<T>> items;  /// Selected Item  final T? selectedItem;  /// Label Title text  final String? labelText;  /// On property changed  final void Function(T? value)? onChanged;}