import 'package:flutter_basics/core/logger/logger_config.dart';import 'package:flutter_bloc/flutter_bloc.dart';/// [CubitObserver] is for Observer our cubitclass CubitObserver extends BlocObserver {  @override  void onCreate(BlocBase<dynamic> bloc) {    super.onCreate(bloc);    'Cubit created ::: ${bloc.runtimeType}'.logI;  }  @override  void onClose(BlocBase<dynamic> bloc) {    super.onClose(bloc);    'Cubit close ::: ${bloc.runtimeType}'.logI;  }}