import 'package:flutter/cupertino.dart';import 'package:flutter_basics/core/ext/ext_string.dart';import 'package:flutter_basics/core/routes/app_routes.dart';import 'package:flutter_basics/src/error/error_page.dart';import 'package:flutter_basics/src/home/page/home_page.dart';import 'package:flutter_basics/src/home/page/test_page.dart';import 'package:go_router/go_router.dart';/// [rootNavKey] for navigation trackingfinal rootNavKey = GlobalKey<NavigatorState>();/// [appRouter] is responsible for all navigation stufffinal appRouter = GoRouter(  navigatorKey: rootNavKey,  routes: [    GoRoute(      path: AppRoutes.homePage,      name: AppRoutes.homePage,      builder: (context, state) => const HomePage(),    ),    GoRoute(      path: AppRoutes.testPage.path,      name: AppRoutes.testPage,      builder: (context, state) => const TestPage(),    ),  ],  errorBuilder: (context, state) => const ErrorPage(),);