// ignore_for_file: public_member_api_docsimport 'package:flutter/material.dart';/// App Color Extension for Custom theme color name@immutableclass AppColors extends ThemeExtension<AppColors> {  /// Default constructor  const AppColors({    required this.scaffoldBackground,    required this.sideBarColor,  });  final Color scaffoldBackground;  final Color sideBarColor;  @override  ThemeExtension<AppColors> copyWith({    Color? scaffoldBackground,    Color? sideBarColor,  }) {    return AppColors(      scaffoldBackground: scaffoldBackground ?? this.scaffoldBackground,      sideBarColor: sideBarColor ?? this.sideBarColor,    );  }  @override  ThemeExtension<AppColors> lerp(    covariant ThemeExtension<AppColors>? other,    double t,  ) {    if (other is! AppColors) {      return this;    }    return AppColors(      scaffoldBackground:          Color.lerp(scaffoldBackground, other.scaffoldBackground, t)!,      sideBarColor: Color.lerp(sideBarColor, other.sideBarColor, t)!,    );  }}/// Text Theme Color Extensionclass TextThemeColors extends ThemeExtension<TextThemeColors> {  /// Default constructor  const TextThemeColors({    required this.textColor,  });  @override  ThemeExtension<TextThemeColors> copyWith({Color? textColor}) {    return TextThemeColors(textColor: textColor ?? this.textColor);  }  @override  ThemeExtension<TextThemeColors> lerp(    covariant ThemeExtension<TextThemeColors>? other,    double t,  ) {    if (other is! TextThemeColors) {      return this;    }    return TextThemeColors(      textColor: Color.lerp(textColor, other.textColor, t)!,    );  }  final Color textColor;}