import 'package:flutter/material.dart';/// App Dividerclass AppDivider extends StatelessWidget {  /// Default constructor  const AppDivider({required this.height, super.key});  /// Height gap upper side and lower side  final double height;  @override  Widget build(BuildContext context) {    return Column(      mainAxisSize: MainAxisSize.min,      children: [        SizedBox(height: height),        const Divider(),        SizedBox(height: height),      ],    );  }}