import 'package:flutter/material.dart';import 'package:flutter_basics/core/ext/ext_build_context.dart';import 'package:syncfusion_flutter_sliders/sliders.dart';/// App Slider Widgetclass AppSlider extends StatelessWidget {  /// Default constructor  const AppSlider({    required this.valueListenable,    required this.labelText,    super.key,    this.onChanged,  });  /// Value Notifier  final ValueNotifier<double?> valueListenable;  /// Label TExt  final String labelText;  /// On Value changed  final void Function(double value)? onChanged;  @override  Widget build(BuildContext context) {    return Column(      mainAxisSize: MainAxisSize.min,      children: [        Text(labelText),        ValueListenableBuilder(          valueListenable: valueListenable,          builder: (context, double? value, __) {            return SfSlider(              min: 1,              max: 100,              enableTooltip: true,              value: value ?? 1,              activeColor: context.theme.primaryColor,              tooltipShape: const SfPaddleTooltipShape(),              onChanged: (value) {                onChanged?.call(value as double);              },            );          },        ),      ],    );  }}