import 'package:flutter/material.dart';/// App Switch Buttonclass AppSwitch extends StatelessWidget {  /// Default constructor  const AppSwitch({    required this.labelText,    required this.value,    this.onChanged,    super.key,  });  /// Label text  final String labelText;  /// Switch button value  final bool value;  /// On Switch value changed  final ValueChanged<bool>? onChanged;  @override  Widget build(BuildContext context) {    return Row(      mainAxisSize: MainAxisSize.min,      children: [        Switch(          value: value,          onChanged: onChanged,        ),        const SizedBox(width: 8),        //applyHeightToFirstAscent        Flexible(          child: Text(            labelText,            overflow: TextOverflow.ellipsis,            style: const TextStyle(fontSize: 16, fontWeight: FontWeight.w500),          ),        ),      ],    );  }}