import 'package:flutter/material.dart';import 'package:flutter/services.dart';import 'package:flutter_basics/core/ext/ext_build_context.dart';/// Common App Text field for Project usagesclass AppTextField extends StatelessWidget {  /// Default constructor  const AppTextField({    required this.labelText,    this.controller,    this.hintText,    this.maxLines,    this.keyboardType,    this.inputFormatters,    this.onChanged,    super.key,  });  /// Text Editing controller  final TextEditingController? controller;  /// Hint text  final String? hintText;  /// Number of line allowed for maxLine  final int? maxLines;  /// Label Text for title  final String labelText;  /// Text Input type for Keyboard behaviour change  final TextInputType? keyboardType;  /// List of Input formatters  final List<TextInputFormatter>? inputFormatters;  /// For handle on Changed Event  final void Function(String value)? onChanged;  @override  Widget build(BuildContext context) {    return Column(      mainAxisSize: MainAxisSize.min,      crossAxisAlignment: CrossAxisAlignment.start,      children: [        Text(          labelText,          style: const TextStyle(fontSize: 16, fontWeight: FontWeight.w500),        ),        const SizedBox(height: 8),        TextFormField(          controller: controller,          maxLines: maxLines,          style: context.text.bodyMedium,          keyboardType: keyboardType,          inputFormatters: inputFormatters,          decoration: InputDecoration(            hintText: hintText,            hintStyle: const TextStyle(fontSize: 16),          ),          onChanged: onChanged,        ),      ],    );  }}