import 'package:bloc/bloc.dart';import 'package:flutter/cupertino.dart';part 'text_state.dart';/// [TextCubit] for handle all state of Text Pageclass TextCubit extends Cubit<TextState> {  /// Initial/Default constructor  TextCubit() : super(TextInitial());  /// For UI text View and modification  final textController =      TextEditingController(text: 'Welcome to flutter basics');  /// Number of line in which text will display  final maxLineController = TextEditingController(text: '1');  /// Text Overflow value  TextOverflow? overflow;  /// On Text Overflow changed  void onOverflowChanged(TextOverflow? flow) {    overflow = flow;    emit(TextOverflowUpdate());  }}