import 'package:flutter/material.dart';import 'package:flutter_basics/src/text/cubit/text_cubit.dart';import 'package:flutter_basics/src/text/widgets/text_output_widget.dart';import 'package:flutter_basics/src/text/widgets/text_property_widget.dart';import 'package:flutter_bloc/flutter_bloc.dart';/// [TextPage] for all text property usagesclass TextPage extends StatelessWidget {  /// Default constructor  const TextPage({super.key});  @override  Widget build(BuildContext context) {    return BlocProvider(      create: (context) => TextCubit(),      child: const Row(        crossAxisAlignment: CrossAxisAlignment.stretch,        children: [          TextPropertyWidget(),          TextOutputWidget(),        ],      ),    );  }}