import 'package:flutter/material.dart';import 'package:flutter_basics/core/ext/ext_build_context.dart';import 'package:flutter_basics/core/routes/app_routes.dart';import 'package:go_router/go_router.dart';/// Side Bar Widgetclass SideBarWidget extends StatelessWidget {  /// Default constructor  const SideBarWidget({required this.child, super.key});  /// Shell widget  final Widget child;  @override  Widget build(BuildContext context) {    return Scaffold(      body: Row(        children: [          SizedBox(            width: 240,            child: DecoratedBox(              decoration: BoxDecoration(                color: context.appColor.sideBarBgColor,              ),              child: Padding(                padding:                    const EdgeInsets.symmetric(horizontal: 8, vertical: 20),                child: Column(                  crossAxisAlignment: CrossAxisAlignment.stretch,                  children: [                    SideMenuWidget(                      menuName: 'Text',                      onItemTap: () => context.goNamed(AppRoutes.textPage),                    ),                    const SizedBox(height: 12),                    const SideMenuWidget(menuName: 'Text'),                    const SizedBox(height: 12),                    const SideMenuWidget(menuName: 'Text'),                    const SizedBox(height: 12),                    const SideMenuWidget(menuName: 'Text'),                  ],                ),              ),            ),          ),          const VerticalDivider(width: 2),          Expanded(child: child),        ],      ),    );  }}/// For Side bar menu widgetclass SideMenuWidget extends StatelessWidget {  /// Default constructor  const SideMenuWidget({    required this.menuName,    this.onItemTap,    super.key,  });  /// For menu name  final String menuName;  /// For Side menu item tap event  final void Function()? onItemTap;  @override  Widget build(BuildContext context) {    return GestureDetector(      onTap: onItemTap,      behavior: HitTestBehavior.opaque,      child: DecoratedBox(        decoration: BoxDecoration(          borderRadius: BorderRadius.circular(8),          color: context.appColor.sideBarSelectedColor,        ),        child: Padding(          padding: const EdgeInsets.symmetric(horizontal: 12, vertical: 6),          child: Text(            menuName,            style: context.text.bodyMedium,          ),        ),      ),    );  }}