import 'package:flutter/material.dart';import 'package:flutter_basics/core/ext/ext_build_context.dart';import 'package:flutter_basics/core/routes/app_routes.dart';import 'package:go_router/go_router.dart';/// Side Bar Widgetclass SideBarWidget extends StatelessWidget {  /// Default constructor  const SideBarWidget({required this.child, super.key});  /// Shell widget  final Widget child;  @override  Widget build(BuildContext context) {    return Scaffold(      body: Row(        children: [          SizedBox(            width: 220,            child: DecoratedBox(              decoration: BoxDecoration(color: context.appColor.sideBarBgColor),              child: Padding(                  padding: const EdgeInsets.only(left: 8),                  child: Column(                    crossAxisAlignment: CrossAxisAlignment.stretch,                    children: [                      const SizedBox(height: 50),                      SideMenuWidget(                        menuName: 'Text',                        onItemTap: () => context.goNamed(AppRoutes.textPage),                      ),                      const SizedBox(height: 12),                    ],                  )),            ),          ),          const VerticalDivider(width: 2),          Expanded(child: child),        ],      ),    );  }}/// For Side bar menu widgetclass SideMenuWidget extends StatefulWidget {  /// Default constructor  const SideMenuWidget({    required this.menuName,    this.onItemTap,    super.key,  });  /// For menu name  final String menuName;  /// For Side menu item tap event  final void Function()? onItemTap;  @override  State<SideMenuWidget> createState() => _SideMenuWidgetState();}class _SideMenuWidgetState extends State<SideMenuWidget> {  @override  Widget build(BuildContext context) {    return GestureDetector(      onTap: widget.onItemTap,      behavior: HitTestBehavior.opaque,      child: SizedBox(        height: 42,        child: DecoratedBox(          decoration: BoxDecoration(            border: Border(              right: BorderSide(                color: context.appColor.sideBarSelectedColor,                width: 2,              ),            ),          ),          child: Align(            alignment: Alignment.centerLeft,            child: Padding(              padding: const EdgeInsets.symmetric(horizontal: 8),              child: Text(                widget.menuName,                textAlign: TextAlign.start,                style: TextStyle(                  fontSize: 18,                  color: context.appColor.sideBarSelectedColor,                  fontWeight: FontWeight.w500,                  fontFamily: 'Montserrat',                ),              ),            ),          ),        ),      ),    );  }}