import 'package:bloc/bloc.dart';import 'package:meta/meta.dart';part 'wrap_state.dart';/// Wrap Cubit is for Handle Wrap widget's propertyclass WrapCubit extends Cubit<WrapState> {  /// Default constructor  WrapCubit() : super(WrapInitial());  /// Number of Boxes are visible on Screen  int boxes = 3;  /// Add Box by 1  void increaseBox() {    boxes += 1;    emit(WrapBoxIncreaseState());  }}