import 'package:flutter/material.dart';import 'package:flutter_basics/core/widgets/app_divider.dart';import 'package:flutter_basics/src/main/property_widget.dart';import 'package:flutter_basics/src/row/widgets/box_widget.dart';import 'package:flutter_basics/src/wrap/cubit/wrap_cubit.dart';import 'package:flutter_bloc/flutter_bloc.dart';/// Wrap page is for demonstrate the [Wrap] widgetclass WrapPage extends StatelessWidget {  /// Default constructor  const WrapPage({super.key});  @override  Widget build(BuildContext context) {    return BlocProvider(      create: (context) => WrapCubit(),      child: BlocBuilder<WrapCubit, WrapState>(        builder: (context, state) {          final cubit = context.read<WrapCubit>();          return Row(            children: [              PropertyWidget(                children: [                  const SizedBox(height: 24),                  Row(                    children: [                      Text(                        'Number of Boxes : ${cubit.boxes}',                        style: const TextStyle(                          fontSize: 16,                          fontWeight: FontWeight.w500,                        ),                      ),                      const SizedBox(width: 8),                      ElevatedButton(                        onPressed: cubit.increaseBox,                        child: const Text('Add 1'),                      ),                    ],                  ),                  const AppDivider(height: 8),                ],              ),              Expanded(                child: Wrap(                  children: [                    for (int index = 0; index < cubit.boxes; index++) ...[                      const BoxWidget(),                    ],                  ],                ),              ),            ],          );        },      ),    );  }}